mapper.select:
SELECT A.* , IFNULL(SUM(RP.point),0) AS extra__sumReactionPoint, IFNULL(SUM(IF(Rp.point > 0,Rp.point,0)),0) AS extra__goodReactionPoint, IFNULL(SUM(IF(Rp.point < 0,Rp.point,0)),0) AS extra__badReactionPoint FROM ( SELECT A.*, M.nickname AS extra__writer FROM article AS A INNER JOIN `member` AS M ON A.memberId = M.id WHERE 1
AND boardId = #{boardId}
AND title LIKE CONCAT('%',#{searchKeyword},'%')
AND body LIKE CONCAT('%',#{searchKeyword},'%')
AND ( title LIKE CONCAT('%',#{searchKeyword},'%') OR body LIKE CONCAT('%',#{searchKeyword},'%') )
ORDER BY A.id DESC
LIMIT #{limitStart}, #{limitTake}
) AS A LEFT JOIN reactionPoint AS RP ON RP.relTypeCode = 'article' AND A.id = RP.relId GROUP BY A.id

mapper.update:
UPDATE article		

updateDate = NOW(),
title = #{title},
`body`= #{body}

WHERE id = #{id}

UPDATE article		

hitCount = hitCount +1

WHERE id = #{id}

mapper.select:
SELECT IFNULL(SUM(RP.point),0) AS s
FROM reactionPoint AS RP
WHERE RP.relTypeCode = 'article'
AND RP.relId = #{id}
AND RP.memberId = #{memberId}
